import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Card from "../Targetas/Cards/Card/Card";
import styles from "./Favorites.module.css";
import { filterCards, orderCards } from "../Redux/actions";

const Favorites = () => {
  const favorites = useSelector((state) => state.myFavorites);
  const dispatch = useDispatch();

  useEffect(() => {
    // Este c칩digo se ejecutar치 cada vez que cambie el estado `favorites`
    console.log("El estado myFavorites ha cambiado:", favorites);
  }, [favorites]);

  const handleOrder = (event) => {
    dispatch(orderCards(event.target.value));
  };

  const handleFilter = (event) => {
    dispatch(filterCards(event.target.value));
  };

  return (
    <div className={styles.background}>
      <h1>Mis Favoritos</h1>
      <div>
        <select onChange={handleOrder}>
          <option value="order" disabled>
            Orden by
          </option>
          <option value="Ascendente">Ascendente</option>
          <option value="Decendente">Descendente</option>
        </select>
        <select onChange={handleFilter}>
          <option value="filter" disabled>
            Filter by
          </option>
          <option value="Male">Masculino</option>
          <option value="Female">Femenino</option>
          <option value="Genderless">Sin Genero</option>
          <option value="unknown">Desconocido</option>
        </select>
      </div>
      <div className={styles.container}>
        {favorites.map((fav) => (
          <Card key={fav.id} {...fav} isFavorite={true} />
        ))}
      </div>
    </div>
  );
};

export default Favorites;
