import { ADD_FAVORITE, REMOVE_FAVORITE, FILTER, ORDER } from "./action.types.js";


const initialState = {
  myFavorites: [],
  allCharacters: []
}

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_FAVORITE:
      return {
        ...state,
        myFavorites: [...state.myFavorites, action.payload], //se crea un estado nuevo, para no modificar el anterior
        allCharacters:[...state.allCharacters, action.payload]
      }

    case REMOVE_FAVORITE:
      return {
        ...state,
        myFavorites: state.myFavorites.filter((character) => character.id !== action.payload),

      }

    case FILTER:
    const { allCharacters } = state;
      const allCharsFiltered = state.allCharacters.filter((character) => character.gender === action.payload)
      return {
        ...state,
        myFavorites: allCharsFiltered
      }

    case ORDER:
     const sortedCharacters = action.payload === "Ascendente"
    ? state.allCharacters.sort((a, b) => a.id - b.id)
    : state.allCharacters.sort((a, b) => b.id - a.id);
  return {
    ...state,
    allCharacters: sortedCharacters
  };

    default:
      return { ...state }
  }

}

export default rootReducer;
